"use client";

import { FilePdfOutlined } from "@ant-design/icons";
import { Button } from "antd";

export default function PaymentStatusDownloader({ data }) {
  const generateInvoicePdf = async () => {
    const pdfMake = (await import("pdfmake/build/pdfmake")).default;
    const pdfFonts = await import("pdfmake/build/vfs_fonts");
    pdfMake.vfs = pdfFonts.vfs;

    // TODO: Replace with actual base64 logo if needed
    const logoBase64 =
      "data:image/jpeg;base64,";

    const docDefinition = {
      content: [
        // Header with logo and company name
        {
          columns: [
            {
              image: logoBase64,
              width: 80,
            },
            {
              text: "MellyCrochets",
              style: "companyName",
              alignment: "right",
            },
          ],
        },
        {
          text: "Transaction Details",
          style: "header",
          margin: [0, 20, 0, 10],
        },
        {
          style: "transactionTable",
          table: {
            widths: ["30%", "70%"],
            body: [
              ["Request ID", data.requestId || "N/A"],
              ["Amount", `${data.amount ?? 0} XAF`],
              ["Description", data.description || "N/A"],
              [
                "Status",
                {
                  text: data.status || "N/A",
                  color: data.status === "PENDING" ? "red" : "green",
                },
              ],
              ["Transaction Status", data.transactionStatus || "N/A"],
              [
                "Created At",
                data.createdAt
                  ? new Date(data.createdAt).toLocaleString()
                  : "N/A",
              ],
              ["Transaction Ref", data.transactionRef || "N/A"],
              ["App ID", data.appId || "N/A"],
              ["Payer Note", data.payerNote || "N/A"],
              [
                "Payment Auth URL",
                data.paymentAuthUrl
                  ? {
                      text: "Open Payment Auth",
                      color: "red",
                      link: data.paymentAuthUrl,
                      decoration: "underline",
                    }
                  : "N/A",
              ],
            ],
          },
          layout: {
            hLineColor: () => "#ccc",
            vLineColor: () => "#ccc",
            paddingLeft: () => 8,
            paddingRight: () => 8,
            paddingTop: () => 4,
            paddingBottom: () => 4,
          },
        },
        {
          text:
            "Phone: +237 681 077 051 / 640 922 135\n" +
            "Email: mellycrochets25@gmail.com\n",
          style: "footer",
          margin: [0, 0, 0, 5],
        },
      ],
      styles: {
        header: {
          fontSize: 16,
          bold: true,
          alignment: "left",
        },
        transactionTable: {
          margin: [0, 0, 0, 15],
        },
      },
      defaultStyle: {
        fontSize: 11,
      },
    };

    pdfMake.createPdf(docDefinition).download(`Invoice_${data.orderNo || "transaction"}.pdf`);
  };

  return (
    <Button icon={<FilePdfOutlined />} type="link" onClick={generateInvoicePdf}>
      Print
    </Button>
  );
}
